// To parse this JSON data, do////     final magazine = magazineFromJson(jsonString);import 'dart:convert';List<Magazine> magazineFromJson(String str) => List<Magazine>.from(json.decode(str).map((x) => Magazine.fromJson(x)));String magazineToJson(List<Magazine> data) => json.encode(List<dynamic>.from(data.map((x) => x.toJson())));class Magazine {  Magazine({    this.title,    this.img,    this.link,  });  late String ?title;  final String ?img;  final String ?link;  factory Magazine.fromJson(Map<String, dynamic> json) => Magazine(    title: json["title"],    img: json["img"],    link: json["link"],  );  Map<String, dynamic> toJson() => {    "title": title,    "img": img,    "link": link,  };}